def date_plus_(n):
    today = datetime.datetime.today()
    future_date = today + datetime.timedelta(days=n)
    print('today:', today)
    print('future_date:', future_date.strftime("%Y-%m-%d"))
    return future_date.strftime("%Y-%m-%d")

def _post_client(id):
    try:
        models.TGClient(
            tg_id=id,
            username='',
        ).save()

        return True
    except Exception as e:
        print(e)
        return False
    
def _post_balance(id):
    try:
        models.Balance(
            tg_id=id,
            tariff='Free Trial',
            next_payment=date_plus_(3),
        ).save()

        return True
    except Exception as e:
        print(e)
        return False

